      =0 {He has no items.}
      one {He has one item.}
      other {He has # items.}
    }
  }
  female {
    {itemCount, plural,
      =0 {She has no items.}
      one {She has one item.}
      other {She has # items.}
    }
  }
  other {
    {itemCount, plural,
      =0 {They have no items.}
      one {They have one item.}
      other {They have # items.}
    }
  }
}

